    public CompletableFuture<Result> getFestivals(String year) {
        return CompletableFuture.supplyAsync(() -> {
            Response response = FestivalListDao.getInstance().getFestivalList(year);
            if (response.getSuccess()) {
                return ok(Json.toJson(response));
            } else {
                return badRequest(Json.toJson(response));
            }
        }, ec.current());
    }

